const products=[{id:1,title:"Hazardouts teen 1",price:2e3,path:"img/hazardous_teen_178x200.jpg"},{id:2,title:"Hazardouts teen 2",price:20,path:"img/hazardous_teen_178x200.jpg"},{id:3,title:"Hazardouts teen 3",price:200,path:"img/hazardous_teen_178x200.jpg"},{id:4,title:"Hazardouts teen 4",price:50,path:"img/hazardous_teen_178x200.jpg"}],renderProduct=t=>`<div class="product-card">\n                <h3 class="product-card__title">${t.title}</h3>\n                <p class="product-card__tally">\n                  Price: <span class="product-card__price">${t.price}</span> &#8364;\n                </p>\n                <img class="product-card__img" src=${t.path} width="178" height="200" alt="Product picture">\n                <button class="product-card__buy-btn">Купить</button>\n                <p class="product-card__tally">\n                  Article: <span class="product-card__id">${t.id}</span>\n                </p>\n            </div>`,renderCatalogue=t=>{const a=t.map(t=>renderProduct(t));document.querySelector(".catalogue").innerHTML=a.join("")};renderCatalogue(products);